Script.script:
function echoCom()
  FEDUI.chatWindow:cecho('<ansiCyan>' .. gmcp.comm.com.from .. ' comms: "' .. gmcp.comm.com.message .. '"\n')
end

function echoTell()
  FEDUI.chatWindow:cecho('<ansiRed>' .. gmcp.comm.tell.from .. ' tight beams you: "' .. gmcp.comm.tell.message .. '"\n')
end

function echoSay()
  FEDUI.chatWindow:cecho('<ansiCyan>' .. gmcp.comm.say.from .. ' says: "' .. gmcp.comm.say.message .. '"\n')
end
